
public class Stringdemo {

	public static void main(String[] args) {
		String s1="10";
		String s2="20";
		String s3=s1+s2;
		int x= Integer.parseInt(s1);	//conversion from string to integer
		int y= Integer.parseInt(s2);	//conversion from string to integer
		int z=x+y;
		System.out.println(s1);
		System.out.println(s2);
		System.out.println(s3);
		
		System.out.println(z); // converted results of integer
		
		// TODO Auto-generated method stub

	}

}
